void CCVertInput(inout StandardVertInput In)
{
    In.position = vec4(a_position, 1.0);
    In.normal = a_normal;
    In.tangent = a_tangent;
  #if CC_USE_MORPH
    applyMorph(In.position, In.normal, In.tangent);
  #endif
  #if CC_USE_SKINNING
    CCSkin(In.position, In.normal, In.tangent);
  #endif
}
#if !USE_INSTANCING
